export class Product {
  constructor(private product: IAPProduct) {
  }

  get term(): string {
    return this.product.billingPeriod + ' ' + this.product.billingPeriodUnit;
  }

  get price(): string {
    return this.product.price;
  }

  get displayLength() {
    return `${this.product.billingPeriod} ${this.product.billingPeriodUnit}`;
  }

  get subLengthMonths() {
    if (this.product.billingPeriodUnit === 'Month') {
      return this.product.billingPeriod;
    }
    if (this.product.billingPeriodUnit === 'Year') {
      return this.product.billingPeriod * 12;
    }
  }

  get monthlyPrice() {
    const mainPrice = (+this.product.priceMicros / 1000000).toFixed(2);
    const monthlyPrice = ((+this.product.priceMicros / 1000000 / this.subLengthMonths) - .005).toFixed(2);
    return this.product.price.replace(mainPrice, monthlyPrice);
  }

  get plan() {
    if (this.product.billingPeriodUnit === 'Month') {
      return this.product.billingPeriod === 1 ? 'monthly' : 'quarterly';
    }
    if (this.product.billingPeriodUnit === 'Year') {
      return 'yearly';
    }
  }
}

